void VlanDataTypes::resize(OpcUa_UInt32 length)
{
    if (length < m_noOfElements)
    {
        if (length != 0)
        {
            for (OpcUa_UInt32 i = length; i < m_noOfElements; i++)
            {
                MoxaClassBased_VlanDataType_Clear(&(m_data[i]));
            }

            MoxaClassBased_VlanDataType* newData = (MoxaClassBased_VlanDataType*)OpcUa_ReAlloc(m_data, length * sizeof(MoxaClassBased_VlanDataType));
            if (newData)
            {
                m_data = newData;
                m_noOfElements = length;
            }
        }
        else
        {
            clear();
        }
    }
    else if (length > m_noOfElements)
    {
        MoxaClassBased_VlanDataType* newData = (MoxaClassBased_VlanDataType*)OpcUa_ReAlloc(m_data, length * sizeof(MoxaClassBased_VlanDataType));
        if (newData)
        {
            for (OpcUa_UInt32 i = m_noOfElements; i < length; i++)
            {
                MoxaClassBased_VlanDataType_Initialize(&(newData[i]));
            }
            m_data = newData;
            m_noOfElements = length;
        }
    }
}

void VlanDataTypes::clear()
{
    if (m_data)
    {
        for (OpcUa_UInt32 i = 0; i < m_noOfElements; i++)
        {
            MoxaClassBased_VlanDataType_Clear(&(m_data[i]));
        }
        OpcUa_Free(m_data);
        m_data = (MoxaClassBased_VlanDataType*)OpcUa_Null;
    }
    m_noOfElements = 0;
}

