/** Uses the NodeAccessInfo of pOther and its children. */
void DeviceFolderTypeBase::useAccessInfoFromInstance(DeviceFolderTypeBase *pOther)
{
    OpcUa::FolderType::useAccessInfoFromInstance(pOther);

    // children
    m_pAddBridgeMethod->useAccessInfoFrom(pOther->m_pAddBridgeMethod);
    m_pAddEndStationMethod->useAccessInfoFrom(pOther->m_pAddEndStationMethod);
    m_pRemoveDeviceMethod->useAccessInfoFrom(pOther->m_pRemoveDeviceMethod);
}

/** Set the NodeAccessInfo for this node and all of its children. */
void DeviceFolderTypeBase::setAccessInfoWithChildren(NodeAccessInfo* pNodeAccessInfo)
{
    OpcUa::FolderType::setAccessInfoWithChildren(pNodeAccessInfo);

    // children
    m_pAddBridgeMethod->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pAddEndStationMethod->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pRemoveDeviceMethod->setAccessInfoWithChildren(pNodeAccessInfo);
}


