/** Returns the type definition NodeId for the ProgramStateMachineType
*/
UaNodeId ProgramStateMachineTypeBase::typeDefinitionId() const
{
    UaNodeId ret(MoxaClassBasedId_ProgramStateMachineType, NodeManagerMoxaClassBasedNS::getTypeNamespace());
    return ret;
}

/**
 *  Sets the ErrorCode value
 */
void ProgramStateMachineTypeBase::setErrorCode(OpcUa_UInt32 ErrorCode)
{
    UaVariant value;
    value.setUInt32(ErrorCode);
    UaDataValue dataValue;
    dataValue.setValue(value, OpcUa_True, OpcUa_True);
    m_pErrorCode->setValue(NULL, dataValue, OpcUa_False);
}

/**
 *  Returns the value of ErrorCode
 */
OpcUa_UInt32 ProgramStateMachineTypeBase::getErrorCode() const
{
    UaVariant defaultValue;
    OpcUa_UInt32 ret = 0;
    UaDataValue dataValue(m_pErrorCode->value(NULL));
    defaultValue = *dataValue.value();
    defaultValue.toUInt32(ret);
    return ret;
}

/**
 *  Sets the ErrorMessage value
 */
void ProgramStateMachineTypeBase::setErrorMessage(const UaString& ErrorMessage)
{
    UaVariant value;
    value.setString(ErrorMessage);
    UaDataValue dataValue;
    dataValue.setValue(value, OpcUa_True, OpcUa_True);
    m_pErrorMessage->setValue(NULL, dataValue, OpcUa_False);
}

/**
 *  Returns the value of ErrorMessage
 */
UaString ProgramStateMachineTypeBase::getErrorMessage() const
{
    UaVariant defaultValue;
    UaString ret;
    UaDataValue dataValue(m_pErrorMessage->value(NULL));
    defaultValue = *dataValue.value();
    ret = defaultValue.toString();
    return ret;
}

