/** Uses the NodeAccessInfo of pOther and its children. */
void ProgramStateMachineTypeBase::useAccessInfoFromInstance(ProgramStateMachineTypeBase *pOther)
{
    OpcUa::FiniteStateMachineType::useAccessInfoFromInstance(pOther);

    // children
    m_pCompleted->useAccessInfoFromInstance(pOther->m_pCompleted);
    m_pCompletedToRunning->useAccessInfoFromInstance(pOther->m_pCompletedToRunning);
    m_pFailed->useAccessInfoFromInstance(pOther->m_pFailed);
    m_pFailedToRunning->useAccessInfoFromInstance(pOther->m_pFailedToRunning);
    m_pReady->useAccessInfoFromInstance(pOther->m_pReady);
    m_pReadyToRunning->useAccessInfoFromInstance(pOther->m_pReadyToRunning);
    m_pRunning->useAccessInfoFromInstance(pOther->m_pRunning);
    m_pRunningToCompleted->useAccessInfoFromInstance(pOther->m_pRunningToCompleted);
    m_pRunningToFailed->useAccessInfoFromInstance(pOther->m_pRunningToFailed);
    m_pRunningToReady->useAccessInfoFromInstance(pOther->m_pRunningToReady);
    m_pErrorCode->useAccessInfoFromInstance(pOther->m_pErrorCode);
    m_pErrorMessage->useAccessInfoFromInstance(pOther->m_pErrorMessage);
}

/** Set the NodeAccessInfo for this node and all of its children. */
void ProgramStateMachineTypeBase::setAccessInfoWithChildren(NodeAccessInfo* pNodeAccessInfo)
{
    OpcUa::FiniteStateMachineType::setAccessInfoWithChildren(pNodeAccessInfo);

    // children
    m_pCompleted->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pCompletedToRunning->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pFailed->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pFailedToRunning->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pReady->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pReadyToRunning->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pRunning->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pRunningToCompleted->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pRunningToFailed->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pRunningToReady->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pErrorCode->setAccessInfoWithChildren(pNodeAccessInfo);
    m_pErrorMessage->setAccessInfoWithChildren(pNodeAccessInfo);
}


UaStatus ProgramStateMachineTypeBase::setProgramState(ProgramStateMachineTypeBase::ProgramState state)
{
    UaNodeId currentTransitionId;
    UaLocalizedText ltTransition;
    ProgramState currentState = getProgramState();
    switch (currentState)
    {
    case Completed:
        break;
    case Failed:
        break;
    case Ready:
        break;
    case Running:
        break;
    }
    return OpcUa_Bad;
}
ProgramStateMachineTypeBase::ProgramState ProgramStateMachineTypeBase::getProgramState()
{
    ProgramState ret = Completed;

    UaNodeId state(getCurrentStateNode()->getIdNodeId());
    OpcUa_UInt16 nsTypeIdx = MoxaClassBased::NodeManagerMoxaClassBasedNS::getTypeNamespace();

    if (state.identifierType() == OpcUa_IdentifierType_Numeric && state.namespaceIndex() == nsTypeIdx)
    {
        switch (state.identifierNumeric())
        {
        case MoxaClassBasedId_ProgramStateMachineType_Completed:
            ret = Completed;
            break;
        case MoxaClassBasedId_ProgramStateMachineType_Failed:
            ret = Failed;
            break;
        case MoxaClassBasedId_ProgramStateMachineType_Ready:
            ret = Ready;
            break;
        case MoxaClassBasedId_ProgramStateMachineType_Running:
            ret = Running;
            break;
        default:
            break;
        }
    }
    return ret;
}
